function menuBurger() {
    const iconMenu = document.querySelector('.header__menu-icon');
    const menuBody = document.querySelector('.menu__body');
    if (iconMenu) {
        iconMenu.addEventListener("click", function (e) {
            document.body.classList.toggle('lock');
            iconMenu.classList.toggle('active');
            menuBody.classList.toggle('active');
        })
    }
}

function moveElement() {
    const elementsToMove = document.querySelectorAll('[data-move]');

    if (elementsToMove.length > 0) {
        for (let index = 0; index < elementsToMove.length; index++) {
            const element = elementsToMove[index];
            const elementName = element.dataset.move.split(', ')[0];
            const screenWidth = element.dataset.move.split(', ')[1];
            const firstPosition = document.querySelector(`[data-desk*=${elementName}]`);
            const lastPosition = document.querySelector(`[data-mob*=${elementName}]`);

            function move() {
                let position;
                if (window.innerWidth <= screenWidth) {
                    if (lastPosition.dataset.mob.split(', ')[1]) {
                        position = lastPosition.dataset.mob.split(', ')[1];
                    } else {
                        position = 'append';
                    }
                    moveObj(element, lastPosition, position);
                } else {
                    if (firstPosition.dataset.desk.split(', ')[1]) {
                        position = firstPosition.dataset.desk.split(', ')[1];
                    } else {
                        position = 'append';
                    }
                    moveObj(element, firstPosition, position);
                }
            }

            function moveObj($elem, $target, $position = 'append') {
                if ($position == 'after') {
                    $target.after($elem);
                } else if ($position == 'before') {
                    $target.before($elem);
                } else if ($position == 'append') {
                    $target.append($elem);
                } else if ($position == 'prepend') {
                    $target.prepend($elem);
                }
            }

            move();

            window.addEventListener('resize', function () {
                move();
            });
        }
    }
}

function lazyLoading() {
    const images = document.querySelectorAll('img[data-src], source[data-srcset]');

    const imageObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const target = entry.target;
                if (target.dataset.src) {
                    target.setAttribute('src', target.dataset.src);
                    target.removeAttribute('data-src');
                }
                if (target.dataset.srcset) {
                    target.setAttribute('srcset', target.dataset.srcset);
                    target.removeAttribute('data-srcset');
                }
                observer.unobserve(target);
            }
        });
    }, {
        threshold: 0.3
    });

    images.forEach(image => {
        imageObserver.observe(image);
    });
}

function select() {

    const selectBoxes = document.querySelectorAll('.select-box');

    if (selectBoxes.length > 0) {
        for (let index = 0; index < selectBoxes.length; index++) {
            const selectBox = selectBoxes[index];
            const selected = selectBox.querySelector('.selected');
            const optionsContainer = selectBox.querySelector('.options-container');
            const optionsList = selectBox.querySelectorAll('.option');

            selected.addEventListener('click', () => {
                if (!optionsContainer.classList.contains('_active')) {
                    selectBoxes.forEach((el) => {
                        el.querySelector('.options-container').classList.remove('_active')
                    });
                }
                optionsContainer.classList.toggle('_active');
                if (optionsContainer.classList.contains('_active')) {
                    selected.classList.add('_active');
                } else {
                    selected.classList.remove('_active');
                }
            });

            optionsList.forEach(o => {
                o.addEventListener('click', () => {
                    selected.querySelector('span').innerHTML = o.querySelector('label').innerHTML;
                    o.querySelector('input').checked = true;
                    optionsContainer.classList.remove('_active');
                    selected.classList.remove('_active');
                });
            });

        }
    }
}

function checkbox() {
    const checkbox = document.querySelectorAll('.checkbox');

    if (checkbox.length > 0) {
        for (let index = 0; index < checkbox.length; index++) {
            const check = checkbox[index];
            const input = check.querySelector('input');
            if (input.checked) {
                check.classList.add('active');
            }

            check.addEventListener('click', function () {
                if (check.classList.contains('active')) {
                    check.classList.remove('active');
                    input.checked = false;
                } else {
                    check.classList.toggle('active');
                    input.checked = true;
                }
            })
        }
    }
}

function starRating() {
    const ratings = document.querySelectorAll('.rating');

    if (ratings.length > 0) {
        initRatings();
    }

    function initRatings() {
        let ratingActive, ratingValue;
        for (let index = 0; index < ratings.length; index++) {
            const rating = ratings[index];
            initRating(rating);
        }

        function initRating(rating) {
            initRatingVars(rating);
            setRatingActiveWidth();
        }

        function initRatingVars(rating) {
            ratingActive = rating.querySelector('.rating__active');
            ratingValue = rating.querySelector('.rating__value');
        }

        function setRatingActiveWidth(index = ratingValue.innerHTML) {
            const ratingActiveWidth = index / 0.05;
            ratingActive.style.width = `${ratingActiveWidth}%`;
        }
    }
}

function spollers() {

    //=================
    /*
    Для родителя слойлеров пишем атрибут data-spollers
    Для заголовков слойлеров пишем атрибут data-spoller
    Если нужно включать\выключать работу спойлеров на разных размерах экранов
    пишем параметры ширины и типа брейкпоинта.
    Например: 
    data-spollers="992,max" - спойлеры будут работать только на экранах меньше или равно 992px
    data-spollers="768,min" - спойлеры будут работать только на экранах больше или равно 768px
    
    Если нужно что бы в блоке открывался болько один слойлер добавляем атрибут data-one-spoller
    */

    // SPOLLERS
    const spollersArray = document.querySelectorAll('[data-spollers]');
    if (spollersArray.length > 0) {
        // Получение обычных слойлеров
        const spollersRegular = Array.from(spollersArray).filter(function (item, index, self) {
            return !item.dataset.spollers.split(",")[0];
        });
        // Инициализация обычных слойлеров
        if (spollersRegular.length > 0) {
            initSpollers(spollersRegular);
        }

        // Получение слойлеров с медиа запросами
        const spollersMedia = Array.from(spollersArray).filter(function (item, index, self) {
            return item.dataset.spollers.split(",")[0];
        });

        // Инициализация слойлеров с медиа запросами
        if (spollersMedia.length > 0) {
            const breakpointsArray = [];
            spollersMedia.forEach(item => {
                const params = item.dataset.spollers;
                const breakpoint = {};
                const paramsArray = params.split(",");
                breakpoint.value = paramsArray[0];
                breakpoint.type = paramsArray[1] ? paramsArray[1].trim() : "max";
                breakpoint.item = item;
                breakpointsArray.push(breakpoint);
            });

            // Получаем уникальные брейкпоинты
            let mediaQueries = breakpointsArray.map(function (item) {
                return '(' + item.type + "-width: " + item.value + "px)," + item.value + ',' + item.type;
            });
            mediaQueries = mediaQueries.filter(function (item, index, self) {
                return self.indexOf(item) === index;
            });

            // Работаем с каждым брейкпоинтом
            mediaQueries.forEach(breakpoint => {
                const paramsArray = breakpoint.split(",");
                const mediaBreakpoint = paramsArray[1];
                const mediaType = paramsArray[2];
                const matchMedia = window.matchMedia(paramsArray[0]);

                // Объекты с нужными условиями
                const spollersArray = breakpointsArray.filter(function (item) {
                    if (item.value === mediaBreakpoint && item.type === mediaType) {
                        return true;
                    }
                });
                // Событие
                matchMedia.addListener(function () {
                    initSpollers(spollersArray, matchMedia);
                });
                initSpollers(spollersArray, matchMedia);
            });
        }
        // Инициализация
        function initSpollers(spollersArray, matchMedia = false) {
            spollersArray.forEach(spollersBlock => {
                spollersBlock = matchMedia ? spollersBlock.item : spollersBlock;
                if (matchMedia.matches || !matchMedia) {
                    spollersBlock.classList.add('_init');
                    initSpollerBody(spollersBlock);
                    spollersBlock.addEventListener("click", setSpollerAction);
                } else {
                    spollersBlock.classList.remove('_init');
                    initSpollerBody(spollersBlock, false);
                    spollersBlock.removeEventListener("click", setSpollerAction);
                }
            });
        }
        // Работа с контентом
        function initSpollerBody(spollersBlock, hideSpollerBody = true) {
            const spollerTitles = spollersBlock.querySelectorAll('[data-spoller]');
            if (spollerTitles.length > 0) {
                spollerTitles.forEach(spollerTitle => {
                    if (hideSpollerBody) {
                        spollerTitle.removeAttribute('tabindex');
                        if (!spollerTitle.classList.contains('_active')) {
                            spollerTitle.nextElementSibling.hidden = true;
                        }
                    } else {
                        spollerTitle.setAttribute('tabindex', '-1');
                        spollerTitle.nextElementSibling.hidden = false;
                    }
                });
            }
        }
        function setSpollerAction(e) {
            const el = e.target;
            if (el.hasAttribute('data-spoller') || el.closest('[data-spoller]')) {
                const spollerTitle = el.hasAttribute('data-spoller') ? el : el.closest('[data-spoller]');
                const spollersBlock = spollerTitle.closest('[data-spollers]');
                const oneSpoller = spollersBlock.hasAttribute('data-one-spoller') ? true : false;
                if (!spollersBlock.querySelectorAll('._slide').length) {
                    if (oneSpoller && !spollerTitle.classList.contains('_active')) {
                        hideSpollersBody(spollersBlock);
                    }
                    spollerTitle.classList.toggle('_active');
                    spollerTitle.parentElement.classList.toggle('_active');
                    _slideToggle(spollerTitle.nextElementSibling, 500);
                }
                e.preventDefault();
            }
        }
        function hideSpollersBody(spollersBlock) {
            const spollerActiveTitle = spollersBlock.querySelector('[data-spoller]._active');
            if (spollerActiveTitle) {
                spollerActiveTitle.classList.remove('_active');
                spollerActiveTitle.parentElement.classList.remove('_active');
                _slideUp(spollerActiveTitle.nextElementSibling, 500);
            }
        }
    }


    //SlideToggle
    let _slideUp = (target, duration = 500) => {
        if (!target.classList.contains('_slide')) {
            target.classList.add('_slide');
            target.style.transitionProperty = 'height, margin, padding';
            target.style.transitionDuration = duration + 'ms';
            target.style.height = target.offsetHeight + 'px';
            target.offsetHeight;
            target.style.overflow = 'hidden';
            target.style.height = 0;
            target.style.paddingTop = 0;
            target.style.paddingBottom = 0;
            target.style.marginTop = 0;
            target.style.marginBottom = 0;
            window.setTimeout(() => {
                target.hidden = true;
                target.style.removeProperty('height');
                target.style.removeProperty('padding-top');
                target.style.removeProperty('padding-bottom');
                target.style.removeProperty('margin-top');
                target.style.removeProperty('margin-bottom');
                target.style.removeProperty('overflow');
                target.style.removeProperty('transition-duration');
                target.style.removeProperty('transition-property');
                target.classList.remove('_slide');
            }, duration);
        }
    }
    let _slideDown = (target, duration = 500) => {
        if (!target.classList.contains('_slide')) {
            target.classList.add('_slide');
            if (target.hidden) {
                target.hidden = false;
            }
            let height = target.offsetHeight;
            target.style.overflow = 'hidden';
            target.style.height = 0;
            target.style.paddingTop = 0;
            target.style.paddingBottom = 0;
            target.style.marginTop = 0;
            target.style.marginBottom = 0;
            target.offsetHeight;
            target.style.transitionProperty = "height, margin, padding";
            target.style.transitionDuration = duration + 'ms';
            target.style.height = height + 'px';
            target.style.removeProperty('padding-top');
            target.style.removeProperty('padding-bottom');
            target.style.removeProperty('margin-top');
            target.style.removeProperty('margin-bottom');
            window.setTimeout(() => {
                target.style.removeProperty('height');
                target.style.removeProperty('overflow');
                target.style.removeProperty('transition-duration');
                target.style.removeProperty('transition-property');
                target.classList.remove('_slide');
            }, duration);
        }
    }
    let _slideToggle = (target, duration = 500) => {
        if (target.hidden) {
            return _slideDown(target, duration);
        } else {
            return _slideUp(target, duration);
        }
    }
}

