$(document).on('click', (e) => {
    var mobMenuLink = $(e.target).closest('.menu__link_mob');
    if (mobMenuLink.length) {
        showSubmenu(e, mobMenuLink);
    }

    var showMoreBtn = $(e.target).closest('.text__more');
    if (showMoreBtn.length) {
        showMoreText(showMoreBtn);
    }
})

function showSubmenu(event, link) {
    event.preventDefault();
    link.toggleClass('active');
    var height = link.next()[0].scrollHeight;

    if (link.hasClass('active')) {
        link.next().css('max-height', height);
    } else {
        link.next().removeAttr('style');
    }
}

function showMoreText(btn) {
    var text = btn.prev();
    var height = text[0].scrollHeight;

    btn.addClass('del');
    text.addClass('active');
    text.css('max-height', height);

    setTimeout(function () {
        text.css('max-height', '100%');
        btn.remove();
    }, 300);
}

